export const pxToUnit=({px:t,unit:n="px",dpi:e})=>{switch(n){case"pt":default:return t;case"mm":return t/(e/25.4);case"cm":return t/(e/2.54);case"in":return t/e}};export const pxToUnitRounded=({px:t,precious:n=2,dpi:e=72,unit:i="px"})=>parseFloat(pxToUnit({px:t,dpi:e,unit:i}).toFixed(n));export const unitToPx=({unitVal:t,dpi:n,unit:e})=>t/pxToUnit({px:1,unit:e,dpi:n});export const pxToUnitString=t=>{const n=parseFloat(pxToUnit(t).toFixed(1));switch(t.unit){case"pt":return`${Math.round(n)}pt`;case"mm":return`${n}mm`;case"cm":return`${n}cm`;case"in":return`${n}in`;default:return`${Math.round(n)}px`}};